import { useState, useEffect } from "react";
import './App.css';
import Square from './Component/Square';

function App() {

  //box index
  const [board, setBoard] = useState(["", "", "", "", "", "", "", "", ""])
  //player turn to ⭕ or ❌
  const[player, setPlayer] = useState("⭕")

  const handleClick =(square) => {
    alert(`Square ${square} has been clicked`)
  }

  return (
    <div className="App">
     <div className="board">
      <h1 className="title">Let's Play<br/>
        Tic Tac Toe</h1>
       <div className="row">
        <Square 
        chooseSquare={()=>{handleClick(0)}}
        val={0}/>
        <Square 
        chooseSquare={()=>{handleClick(1)}}
        val={1}/>
        <Square 
        chooseSquare={()=>{handleClick(2)}}
        val={2}/>
       </div>
       <div className="row">
       <Square 
        chooseSquare={()=>{handleClick(3)}}
        val={3}/>
        <Square 
        chooseSquare={()=>{handleClick(4)}}
        val={4}/>
        <Square 
        chooseSquare={()=>{handleClick(5)}}
        val={5}/>
       </div>
       <div className="row">
       <Square 
        chooseSquare={()=>{handleClick(6)}}
        val={6}/>
        <Square 
        chooseSquare={()=>{handleClick(7)}}
        val={7}/>
        <Square 
        chooseSquare={()=>{handleClick(8)}}
        val={8}/>
       </div>
     </div>
    </div>
  );
}

export default App;
